import React, { useState, useEffect, useRef } from 'react';
import { NavLink, Routes, Route, useLocation, useNavigate } from 'react-router-dom';
import Home from './home';
import Seat from './seat';
import Mine from './mine';
import SignIn from './signin';
import SignUp from './signup';
import PrivateRoute from './privateRoute';
import './App.css';

function App() {
  const [activeTab, setActiveTab] = useState('home');
  const [seatMenuOpen, setSeatMenuOpen] = useState(false);
  const [profileMenuOpen, setProfileMenuOpen] = useState(false);
  const seatRef = useRef(null);
  const profileRef = useRef(null);
  const location = useLocation();
  const navigate = useNavigate();

  useEffect(() => {
    const path = location.pathname === '/' ? 'home' : location.pathname.replace('/', '');
    if (['home', 'seat', 'mine'].includes(path)) {
      setActiveTab(path);
    }
  }, [location.pathname]);

  useEffect(() => {
    const isLoggedIn = localStorage.getItem('isLoggedIn') === 'true';
    const isAuthPage = ['/signin', '/signup'].includes(location.pathname);
    if (!isLoggedIn && !isAuthPage) {
      navigate('/signin', { replace: true });
    }
  }, [location.pathname, navigate]);

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (seatRef.current && !seatRef.current.contains(event.target)) {
        setSeatMenuOpen(false);
      }
      if (profileRef.current && !profileRef.current.contains(event.target)) {
        setProfileMenuOpen(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('isLoggedIn');
    localStorage.removeItem('savedEmail');
    navigate('/signin', { replace: true });
  };

  const handleEditProfile = () => {
    setProfileMenuOpen(false);
    navigate('/mine');
  };

  const handleSeatBooking = () => {
    setSeatMenuOpen(false);
    navigate('/seat');
  };

  return (
    <div className="app-container">
      {!['/signin', '/signup'].includes(location.pathname) && (
        <nav className="navbar">
          <div className="navbar-left">
            <div className="logo">
              <div className="logo-icon">S</div>
              <span className="logo-text">Smart Seat</span>
            </div>
          </div>

          <div className="nav-links">
            <NavLink 
              to="/" 
              className={({ isActive }) => `nav-item ${isActive ? 'active' : ''}`}
              onClick={() => setActiveTab('home')}
            >
              Home
              <span className="underline-indicator"></span>
            </NavLink>

            <div 
              ref={seatRef}
              className="seat-container"
              onMouseEnter={() => {
                setSeatMenuOpen(true);
              }}
              onMouseLeave={() => setSeatMenuOpen(false)}
            >
              <div 
                className={`nav-item ${activeTab === 'seat' ? 'active' : ''}`}
                onClick={(e) => {
                  e.stopPropagation();
                  setActiveTab('seat');
                  navigate('/seat');
                }}
              >
                Seat
                <span className="underline-indicator"></span>
              </div>

              {seatMenuOpen && (
                <div className="dropdown-menu">
                  <div 
                    className="dropdown-item"
                    onClick={handleSeatBooking}
                  >
                    Book a Seat
                  </div>
                  <div 
                    className="dropdown-item"
                    onClick={() => {
                      setSeatMenuOpen(false);
                      navigate('/seat/records');
                    }}
                  >
                    Booking Records
                  </div>
                </div>
              )}
            </div>

            <NavLink 
              to="/mine" 
              className={({ isActive }) => `nav-item ${isActive ? 'active' : ''}`}
              onClick={() => setActiveTab('mine')}
            >
              Mine
              <span className="underline-indicator"></span>
            </NavLink>
          </div>

          <div 
            ref={profileRef}
            className="profile-container"
            onMouseEnter={() => setProfileMenuOpen(true)}
            onMouseLeave={() => setProfileMenuOpen(false)}
            onClick={(e) => {
              e.stopPropagation();
              navigate('/mine');
            }}
          >
            <div className="profile-image">
              {localStorage.getItem('savedEmail')?.charAt(0).toUpperCase() || 'U'}
            </div>

            {profileMenuOpen && (
              <div className="profile-menu">
                <div 
                  className="dropdown-item"
                  onClick={(e) => {
                    e.stopPropagation();
                    handleEditProfile();
                  }}
                >
                  Edit Profile
                </div>
                <div 
                  className="dropdown-item"
                  onClick={handleLogout}
                >
                  Logout
                </div>
              </div>
            )}
          </div>
        </nav>
      )}

      <Routes>
        <Route path="/signin" element={<SignIn />} />
        <Route path="/signup" element={<SignUp />} />
        <Route 
          path="/" 
          element={
            <PrivateRoute>
              <Home />
            </PrivateRoute>
          } 
        />
        <Route 
          path="/seat" 
          element={
            <PrivateRoute>
              <Seat />
            </PrivateRoute>
          } 
        />
        <Route 
          path="/seat/records" 
          element={
            <PrivateRoute>
              <div>Booking Records</div>
            </PrivateRoute>
          } 
        />
        <Route 
          path="/mine" 
          element={
            <PrivateRoute>
              <Mine />
            </PrivateRoute>
          } 
        />
      </Routes>
    </div>
  );
}

export default App;
