import React, { useState, useEffect } from 'react';
import { NavLink, useNavigate } from 'react-router-dom';
const SignIn = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    email: '',
    password: '',
    rememberMe: false
  });
  useEffect(() => {
    const savedEmail = localStorage.getItem('savedEmail');
    if (savedEmail) {
      setFormData(prev => ({ ...prev, email: savedEmail, rememberMe: true }));
    }
  }, []);
  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));
  };
  const handleSubmit = (e) => {
    e.preventDefault();
    const { email, password, rememberMe } = formData;
    if (email && password) {
      localStorage.setItem('isLoggedIn', 'true');
      if (rememberMe) {
        localStorage.setItem('savedEmail', email);
      } else {
        localStorage.removeItem('savedEmail');
      }
      navigate('/', { replace: true });
    } else {
      alert('请填写完整邮箱和密码！');
    }
  };
  const styles = {
    signinContainer: {
      position: 'relative',
      minHeight: '100vh',
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
      padding: '20px',
      margin: 0,
      boxSizing: 'border-box',
      fontFamily: "'Arial', sans-serif"
    },
    signinBg: {
      position: 'absolute',
      top: 0,
      left: 0,
      width: '100%',
      height: '100%',
      backgroundImage: "url('/bg.jpg')",
      backgroundSize: 'cover',
      backgroundPosition: 'center',
      filter: 'brightness(0.8)',
      zIndex: -1
    },
    signinForm: {
      backgroundColor: 'rgba(255, 255, 255, 0.95)',
      padding: '40px 30px',
      borderRadius: '12px',
      boxShadow: '0 4px 20px rgba(0, 0, 0, 0.15)',
      width: '100%',
      maxWidth: '400px',
      textAlign: 'center'
    },
    systemTitle: {
      fontSize: '24px',
      fontWeight: 700,
      color: '#333',
      marginBottom: '20px',
      margin: 0
    },
    pageTitle: {
      fontSize: '22px',
      fontWeight: 600,
      color: '#333',
      marginBottom: '30px',
      margin: 0
    },
    formGroup: {
      marginBottom: '20px',
      textAlign: 'left'
    },
    formInput: {
      width: '100%',
      padding: '12px 15px',
      border: '1px solid #ddd',
      borderRadius: '6px',
      fontSize: '16px',
      transition: 'border-color 0.3s',
      boxSizing: 'border-box'
    },
    rememberMe: {
      display: 'flex',
      alignItems: 'center',
      marginBottom: '25px',
      textAlign: 'left'
    },
    rememberCheckbox: {
      marginRight: '8px',
      width: '16px',
      height: '16px'
    },
    rememberLabel: {
      fontSize: '14px',
      color: '#666',
      cursor: 'pointer'
    },
    signinBtn: {
      width: '100%',
      padding: '12px',
      backgroundColor: '#4285F4',
      color: 'white',
      border: 'none',
      borderRadius: '6px',
      fontSize: '16px',
      fontWeight: 600,
      cursor: 'pointer',
      transition: 'background-color 0.3s',
      marginBottom: '25px'
    },
    signupLink: {
      fontSize: '14px',
      color: '#666'
    },
    linkText: {
      color: '#4285F4',
      textDecoration: 'none',
      fontWeight: 500,
      marginLeft: '4px'
    }
  };
  return (
    <div style={styles.signinContainer}>
      <div style={styles.signinBg}></div>
      <form style={styles.signinForm} onSubmit={handleSubmit}>
        <h1 style={styles.systemTitle}>Smart Seats System</h1>
        <h2 style={styles.pageTitle}>Sign In</h2>
        <div style={styles.formGroup}>
          <input
            type="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            placeholder="Enter your email"
            required
            style={styles.formInput}
          />
        </div>
        <div style={styles.formGroup}>
          <input
            type="password"
            name="password"
            value={formData.password}
            onChange={handleChange}
            placeholder="Enter your password"
            required
            style={styles.formInput}
          />
        </div>
        <div style={styles.rememberMe}>
          <input
            type="checkbox"
            id="rememberMe"
            name="rememberMe"
            checked={formData.rememberMe}
            onChange={handleChange}
            style={styles.rememberCheckbox}
          />
          <label htmlFor="rememberMe" style={styles.rememberLabel}>Remember me</label>
        </div>
        <button type="submit" style={styles.signinBtn} onMouseOver={(e) => e.target.style.backgroundColor = '#3367D6'} onMouseOut={(e) => e.target.style.backgroundColor = '#4285F4'}>Sign in</button>
        <div style={styles.signupLink}>
          Don't have an account? 
          <NavLink to="/signup" style={styles.linkText} onMouseOver={(e) => e.target.style.textDecoration = 'underline'} onMouseOut={(e) => e.target.style.textDecoration = 'none'}> Sign up</NavLink>
        </div>
      </form>
    </div>
  );
};
export default SignIn;
