}

.back-wall-tables {
  position: absolute;
  bottom: 00px;
  left: 50%;
  transform: translateX(-50%);
  display: flex;
  gap: 100px;
}

.classroom-type2 {
  width: 900px;
  padding: 0px;
  position: relative;
}

.classroom-type2-rows {
  display: flex;
  flex-direction: column-reverse;
  align-items: center;
  gap: 8px;
  position: relative;
  z-index: 2;
}

.classroom-row {
  display: flex;
