}

.selection-summary h3 {
  margin-bottom: 1.2rem;
  color: #0f172a;
  font-size: 1.1rem;
  font-weight: 600;
}

.selection-summary p {
  margin-bottom: 0.6rem;
  color: #334155;
  font-size: 0.95rem;
}

.selection-summary .selected-seats {
  margin-top: 1rem;
  padding: 1rem;
  background-color: #f1f5f9;
  border-radius: 6px;
}

.selection-summary .selected-seats p {
  margin-bottom: 0.3rem;
}

.seat-content h1 {
