  margin-bottom: 2.2rem;
  font-size: 2rem;
  font-weight: 700;
}

.empty-state {
  display: flex;
  align-items: center;
  justify-content: center;
  height: calc(100% - 5rem);
  min-height: 500px;
  background-color: #ffffff;
  border-radius: 8px;
  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.06);
  color: #64748b;
  font-size: 1.1rem;
  text-align: center;
  padding: 2rem;
}

.location-view h2 {
  color: #1e40af;
  margin-bottom: 1.8rem;
  font-size: 1.4rem;
  font-weight: 600;
}

.seat-map {
  background-color: #ffffff;
  border-radius: 8px;
  min-height: 500px;
  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.06);
  padding: 2.5rem;
  display: flex;
  align-items: center;
  justify-content: center;
  overflow: auto;
}

.placeholder-content {
  color: #64748b;
  text-align: center;
  font-size: 1.05rem;
  max-width: 60%;
}

.table {
  background-color: #e2e8f0;
  margin: 20px auto;
  position: relative;
}

.horizontal-table {
  width: 180px;
  height: 60px;
}

.vertical-table {
  width: 60px;
  height: 180px;
}

.seat {
  width: 30px;
  height: 30px;
  border-radius: 50%;
  background-color: #94a3b8;
  color: white;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 12px;
  cursor: pointer;
  transition: all 0.2s ease;
  margin: 0 5px;
}

.seat.selected {
  background-color: #1e40af;
}

.table-seats {
  display: flex;
  justify-content: center;
  position: absolute;
}

.top-seats {
  top: -40px;
  left: 0;
  right: 0;
}

.bottom-seats {
  bottom: -40px;
  left: 0;
  right: 0;
}

.left-seats {
  top: 0;
  bottom: 0;
  left: -40px;
  flex-direction: column;
  justify-content: center;
}

.right-seats {
  top: 0;
  bottom: 0;
  right: -40px;
  flex-direction: column;
  justify-content: center;
}

.canteen-layout {
  display: flex;
  width: 100%;
  justify-content: space-around;
}

.canteen-column {
  display: flex;
  flex-direction: column;
  align-items: center;
}

.library-layout {
  display: flex;
  width: 100%;
  justify-content: space-around;
}

.library-column {
  display: flex;
  flex-direction: column;
  align-items: center;
}

.classroom-type1 {
  position: relative;
  width: 800px;
  height: 600px;
  border: 1px solid #e2e8f0;
  padding: 40px;
}

.left-wall-table {
  position: absolute;
  left: 40px;
  top: 50%;
  transform: translateY(-50%);
