.right-wall-table {
  position: absolute;
  right: 40px;
  top: 50%;
  transform: translateY(-50%);
}

.front-wall-tables {
  position: absolute;
  top: 40px;
  left: 50%;
  transform: translateX(-50%);
  display: flex;
  gap: 40px;
