.classroom-type2 {
  width: 600px;
  padding: 20px;
}

.classroom-type2-rows {
  display: flex;
  flex-direction: column;
  align-items: center;
}

.classroom-row {
  display: flex;
  gap: 150px;
  margin-bottom: 10px;
}

.staircase-row {
  background-color: #f1f5f9;
  padding: 5px 20px;
  border-radius: 4px;
