import React, { useState } from 'react';
import { NavLink, useNavigate } from 'react-router-dom';
const SignUp = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    identity: '',
    email: '',
    password: '',
    confirmPassword: '',
    agreePolicy: false
  });
  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: type === 'checkbox' || type === 'radio' ? checked : value
    }));
  };
  const handleSubmit = (e) => {
    e.preventDefault();
    const { identity, email, password, confirmPassword, agreePolicy } = formData;
    if (!identity) {
      alert('请选择身份（Student/Lecturer）！');
      return;
    }
    if (!email || !password || !confirmPassword) {
      alert('请填写完整表单！');
      return;
    }
    if (password !== confirmPassword) {
      alert('两次输入的密码不一致！');
      return;
    }
    if (!agreePolicy) {
      alert('请同意隐私政策（Privacy Policy）！');
      return;
    }
    alert('注册成功！请登录');
    navigate('/signin', { replace: true });
  };
  const styles = {
    signupContainer: {
      position: 'relative',
      minHeight: '100vh',
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
      padding: '20px',
      margin: 0,
      boxSizing: 'border-box',
      fontFamily: "'Arial', sans-serif"
    },
    signupBg: {
      position: 'absolute',
      top: 0,
      left: 0,
      width: '100%',
      height: '100%',
      backgroundImage: "url('/bg.jpg')",
      backgroundSize: 'cover',
      backgroundPosition: 'center',
      filter: 'brightness(0.8)',
      zIndex: -1
    },
    signupForm: {
      backgroundColor: 'rgba(255, 255, 255, 0.95)',
      padding: '40px 30px',
      borderRadius: '12px',
      boxShadow: '0 4px 20px rgba(0, 0, 0, 0.15)',
      width: '100%',
      maxWidth: '400px',
      textAlign: 'center'
    },
    systemTitle: {
      fontSize: '24px',
      fontWeight: 700,
      color: '#333',
      marginBottom: '20px',
      margin: 0
    },
    pageTitle: {
      fontSize: '22px',
      fontWeight: 600,
      color: '#333',
      marginBottom: '30px',
      margin: 0
    },
    identitySelection: {
      marginBottom: '30px',
      textAlign: 'left'
    },
    selectionLabel: {
      fontSize: '16px',
      color: '#333',
      fontWeight: 500,
      marginBottom: '10px',
      display: 'block'
    },
    identityOptions: {
      display: 'flex',
      gap: '20px'
    },
    identityLabel: {
      display: 'flex',
      alignItems: 'center',
      fontSize: '16px',
      color: '#666',
      cursor: 'pointer'
    },
    identityRadio: {
      marginRight: '8px',
      width: '16px',
      height: '16px'
    },
    formGroup: {
      marginBottom: '20px',
      textAlign: 'left'
    },
    formInput: {
      width: '100%',
      padding: '12px 15px',
      border: '1px solid #ddd',
      borderRadius: '6px',
      fontSize: '16px',
      transition: 'border-color 0.3s',
      boxSizing: 'border-box'
    },
    policyAgree: {
      display: 'flex',
      alignItems: 'flex-start',
      marginBottom: '25px',
      textAlign: 'left',
      fontSize: '14px',
      color: '#666'
    },
    policyCheckbox: {
      marginRight: '8px',
      marginTop: '2px',
      width: '16px',
      height: '16px'
    },
    signupBtn: {
      width: '100%',
      padding: '12px',
      backgroundColor: '#4285F4',
      color: 'white',
      border: 'none',
      borderRadius: '6px',
      fontSize: '16px',
      fontWeight: 600,
      cursor: 'pointer',
      transition: 'background-color 0.3s',
      marginBottom: '25px'
    },
    signinLink: {
      fontSize: '14px',
      color: '#666'
    },
    linkText: {
      color: '#4285F4',
      textDecoration: 'none',
      fontWeight: 500,
      marginLeft: '4px'
    }
  };
  return (
    <div style={styles.signupContainer}>
      <div style={styles.signupBg}></div>
      <form style={styles.signupForm} onSubmit={handleSubmit}>
        <h1 style={styles.systemTitle}>Smart Seats System</h1>
        <h2 style={styles.pageTitle}>Sign Up</h2>
        <div style={styles.identitySelection}>
          <p style={styles.selectionLabel}>Select Identity</p>
          <div style={styles.identityOptions}>
            <label style={styles.identityLabel}>
              <input
                type="radio"
                name="identity"
                value="student"
                checked={formData.identity === 'student'}
                onChange={handleChange}
                style={styles.identityRadio}
              />
              Student
            </label>
            <label style={styles.identityLabel}>
              <input
                type="radio"
                name="identity"
                value="lecturer"
                checked={formData.identity === 'lecturer'}
                onChange={handleChange}
                style={styles.identityRadio}
              />
              Lecturer
            </label>
          </div>
        </div>
        <div style={styles.formGroup}>
          <input
            type="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            placeholder="Enter your email"
            required
            style={styles.formInput}
          />
        </div>
        <div style={styles.formGroup}>
          <input
            type="password"
            name="password"
            value={formData.password}
            onChange={handleChange}
            placeholder="Enter your password"
            required
            style={styles.formInput}
          />
        </div>
        <div style={styles.formGroup}>
          <input
            type="password"
            name="confirmPassword"
            value={formData.confirmPassword}
            onChange={handleChange}
            placeholder="Confirm your password"
            required
            style={styles.formInput}
          />
        </div>
        <div style={styles.policyAgree}>
          <input
            type="checkbox"
            id="agreePolicy"
            name="agreePolicy"
            checked={formData.agreePolicy}
            onChange={handleChange}
            style={styles.policyCheckbox}
          />
          <label htmlFor="agreePolicy" style={styles.policyLabel}>
            I agree to the Privacy Policy
          </label>
        </div>
        <button type="submit" style={styles.signupBtn} onMouseOver={(e) => e.target.style.backgroundColor = '#3367D6'} onMouseOut={(e) => e.target.style.backgroundColor = '#4285F4'}>Sign up</button>
        <div style={styles.signinLink}>
          Already have an account? 
          <NavLink to="/signin" style={styles.linkText} onMouseOver={(e) => e.target.style.textDecoration = 'underline'} onMouseOut={(e) => e.target.style.textDecoration = 'none'}> Sign in</NavLink>
        </div>
      </form>
    </div>
  );
};
export default SignUp;
