  font-size: 2rem;
  font-weight: 700;
}

.empty-state {
  display: flex;
  align-items: center;
  justify-content: center;
  height: calc(100% - 5rem);
  min-height: 500px;
  background-color: #ffffff;
  border-radius: 8px;
  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.06);
  color: #64748b;
  font-size: 1.1rem;
  text-align: center;
  padding: 2rem;
}

.location-view h2 {
  color: #1e40af;
  margin-bottom: 1.8rem;
  font-size: 1.4rem;
  font-weight: 600;
}

.seat-map {
  background-color: #ffffff;
  border-radius: 8px;
  min-height: 600px;
  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.06);
  padding: 2.5rem;
  display: flex;
  align-items: center;
  justify-content: center;
  overflow: auto;
}

.table {
  background-color: #e2e8f0;
  margin: 30px auto;
  position: relative;
}

.horizontal-table {
  width: 180px;
  height: 60px;
}

.vertical-table {
  width: 60px;
  height: 180px;
}

.seat {
  width: 30px;
  height: 30px;
  border-radius: 50%;
  background-color: #94a3b8;
  color: white;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 12px;
  cursor: pointer;
  transition: all 0.2s ease;
  margin: 0 8px;
}

.seat.selected {
  background-color: #1e40af;
}

.table-seats {
  display: flex;
  justify-content: center;
  position: absolute;
}

.top-seats {
  top: -40px;
  left: 0;
  right: 0;
}

.bottom-seats {
  bottom: -40px;
  left: 0;
  right: 0;
}

.left-seats {
  top: 0;
  bottom: 0;
  left: -50px;
  flex-direction: column;
  justify-content: center;
  gap: 15px;
}

.right-seats {
  top: 0;
  bottom: 0;
  right: -50px;
  flex-direction: column;
  justify-content: center;
  gap: 15px;
}

.canteen-layout {
  display: flex;
  width: 100%;
  justify-content: space-around;
  padding: 20px;
}

.canteen-column {
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 40px;
}

.library-layout {
  display: flex;
  width: 100%;
  justify-content: space-around;
  padding: 20px;
}

.library-column {
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 40px;
}

.classroom-type1 {
  position: relative;
  width: 1000px;
  height: 700px;
  border: 1px solid #e2e8f0;
  padding: 60px;
}

.classroom-01-07 {
  width: 1200px;
}

.classroom-01-07 .left-wall-table {
  left: 0px;
}

.classroom-01-07 .right-wall-table {
  right: 0px;
}

.classroom-01-07 .front-wall-tables,
.classroom-01-07 .back-wall-tables {
  left: 50%;
  transform: translateX(-50%);
  display: flex;
  gap: 20px;
  width: 600px;
  justify-content: center;
}

.left-wall-table {
  position: absolute;
  left: 60px;
  top: 50%;
  transform: translateY(-50%);
}

.right-wall-table {
  position: absolute;
  right: 60px;
  top: 50%;
  transform: translateY(-50%);
}

.front-wall-tables {
  position: absolute;
  top: 60px;
  left: 50%;
  transform: translateX(-50%);
  display: flex;
  gap: 100px;
}

.back-wall-tables {
  position: absolute;
  bottom: 00px;
  left: 50%;
  transform: translateX(-50%);
  display: flex;
  gap: 100px;
}

.classroom-type2 {
  width: 900px;
  padding: 0px;
  position: relative;
}

.classroom-type2-rows {
  display: flex;
  flex-direction: column-reverse;
  align-items: center;
  gap: 8px;
  position: relative;
  z-index: 2;
}

.classroom-row {
  display: flex;
  justify-content: space-between;
  width: 100%;
  gap: 30px;
}

.seat-group {
  display: flex;
  gap: 0px;
}

.left-group, .right-group {
  display: flex;
  justify-content: center;
}

.staircase-area {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 40%;
  background-color: #f1f5f9;
  z-index: 1;
}

.staircase-row {
  position: relative;
  z-index: 2;
}
